    for (var i = 0; i < obj.length; i++) {
        music_list = {
            'name': obj[i].title,
            'artist': obj[i].author,
            'url': obj[i].url,
            'cover': obj[i].pic,
            'lrc': obj[i].lrc,
            'theme': '#ad7a86'
        };
        data.push(music_list);
    }
    return data;
}


function add_music(old_data, new_data) {
    /* 自定义添加自己喜欢的音乐，如周杰伦的收费音乐 */
    /*    new_data =  [
            {
                "name": "最伟大的作品",
